sources.styles/abstracts/_mixins.scss:
@use 'variables' as *;
@use 'maps' as *;

// =========================================
// BEM
// =========================================

@mixin e($element) {
   &__#{$element} {
      @content;
   }
}

@mixin m($modifier) {
   &--#{$modifier} {
      @content;
   }
}

// =========================================
// flex
// =========================================

@mixin flexy($disp: flex, $dir: null, $wrap: null, $just: null, $align: null) {
   display: $disp;
   flex-direction: $dir;
   flex-wrap: $wrap;
   justify-content: $just;
   align-items: $align;
}

// =========================================
// BG-IMG
// =========================================

@mixin bg-img($img, $position, $size) {
   background-image: url('#{$img}');
   background-repeat: no-repeat;
   background-position: #{$position};
   background-size: #{$size};
}

// =========================================
// pseudo-el
// =========================================

@mixin p-el($el, $width: null, $height: null) {
   @if $el == 'before' or $el == 'after' {
      &::#{$el} {
         content: '';
         position: absolute;
         display: block;
         width: $width;
         height: $height;
         @content;
      }
   } @else {
      @error 'Before or after string required for valid pseudo element.';
   }
}

// =========================================
// media query
// =========================================

@mixin mq($key) {
   @if $key == 'tablet' or $key == 'medium' or $key == 'large' {
      @media (min-width: map-get($breakpoints, $key)) {
         @content;
      }
   } @else {
      @error 'Tablet, medium or large required for valid media query.';
   }
}

// =========================================
// feature query
// =========================================

@mixin fq($prop, $val) {
   @supports (#{$prop}: $val) {
      @content;
   }
}

