  --clr-sacramento-green: 186 96% 10%;
  --clr-dark-green: 186 100% 8%;
  --fs-600: 6.6667rem;
  --fs-500: 4.2667rem;
  --fs-400: 3.2rem;
  --fs-300: 1.2rem;
  --fs-200: 0.9375rem;
  --ff-serif: "Livvic", sans-serif;
  --fw-regular: 400;
  --fw-medium: 500;
  --fw-semi-bold: 600;
  --fw-bold: 700;
  --lh-600: 1;
  --lh-500: 0.875;
  --lh-400: 1;
  --lh-300: 1.5556;
  --lh-200: 1.666;
  --align-mw: 60rem;
  --ct-padding: 3rem 0;
  --gap: 1rem;
  --flow-space: 1rem;
}

/* ----- bg colors ------ */
body {
  background-color: hsl(var(--clr-pm-green));
}

.btn--pm--dark:hover {
  background-color: hsl(var(--clr-dark-green));
}

.small-border::before {
  background-color: hsl(var(--clr-pm-coral));
}

.btn--pm--light:hover, .btn--sd {
  background-color: hsl(var(--clr-pm-white));
}

.hero .small-border::before, .btn--sd:hover {
  background-color: hsl(var(--clr-rapture-blue));
}

.form, .btn--pm--light, .btn--pm--dark {
  background-color: transparent;
}

/* ----- fg colors ------ */
.form, .btn--pm--light, .btn--pm--dark:hover, body {
  color: hsl(var(--clr-pm-white));
}

.btn, .btn--pm--light:hover {
  color: hsl(var(--clr-dark-green));
}

/* ----- font sizes + line heights ------ */
