  font-size: var(--fs-200, 0.9375rem);
  line-height: var(--lh-200, 1.666);
}

.contact {
  font-size: var(--fs-300, 1.2rem);
  line-height: var(--lh-300, 1.5556);
}

/* ----- font weights ------ */
body {
  font-weight: var(--fw-regular, 400);
}

/* ----- text alignment ------ */
.container {
  width: 90%;
  max-width: var(--align-mw);
  margin-inline: auto;
}

.flow > *:where(:not(:first-child)) {
  margin-top: var(--flow-space, 1rem);
}

/* Box sizing rules */
*,
*::before,
*::after {
  box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
  margin: 0;
}

