sources.styles/components/_button.scss:
@use 'abstracts' as *;
@use 'utilities' as *;

.btn {
   @extend %text-dark;
   padding: 1em 3em;
   border-radius: 50px;

   @include m(pm) {
      border: 2px solid;
      text-decoration: none;
      @include m(light) {
         @extend %text-white;
         @extend %bg-transparent;
         border-color: hsl(var(--clr-pm-white));
         &:hover {
            @extend %bg-white;
            @extend %text-dark;
         }
      }

      @include m(dark) {
         @extend %bg-transparent;
         border-color: hsl(var(--clr-dark-green));
         &:hover {
            @extend %bg-green-dark;
            @extend %text-white;
         }
      }
   }

   @include m(sd) {
      @extend %bg-white;
      border: none;
      outline: none;
      &:hover {
         @extend %bg-rapture;
      }
   }

   @include m(disabled) {
      opacity: 0.5;
   }
}

sources.styles/abstracts/_mixins.scss:
@use 'variables' as *;
@use 'maps' as *;

@mixin e($element) {
   &__#{$element} {
      @content;
   }
}

@mixin m($modifier) {
   &--#{$modifier} {
      @content;
   }
}

@mixin flex-parent($direction, $justify, $align) {
   flex-direction: $direction;
   justify-content: $justify;
   align-items: $align;
}

@mixin bg-img($img, $position, $size) {
   background-image: url('#{$img}');
   background-repeat: no-repeat;
   background-position: #{$position};
   background-size: #{$size};
}

@mixin p-el($el, $width: null, $height: null) {
   @if $el == 'before' or $el == 'after' {
      &::#{$el} {
         content: '';
         position: absolute;
         display: block;
         width: $width;
         height: $height;
         @content;
      }
   } @else {
      @error 'Before or after string required for valid pseudo element.';
   }
}

@mixin mq($key) {
   @media (min-width: map-get($breakpoints, $key)) {
      @content;
   }
}

@mixin fq($prop, $val) {
   @supports (#{$prop}: $val) {
      @content;
   }
}

sources.styles/components/_form.scss:
@use 'abstracts' as *;
@use 'utilities' as *;

.form {
   @extend %bg-transparent;
   @extend %text-white;
   width: 100%;
   padding-top: 1rem;
   border: 0 0 0.1px solid hsl(var(--clr-pm-white)) 0;
   outline: none;
   &::placeholder {
      color: hsl(var(--clr-pm-white) / 0.7);
   }
   &:hover,
   &:focus {
      border-bottom-color: hsl(var(--clr-rapture-blue));
   }

   @include m(error) {
      border-bottom-color: hsl(var(--clr-pm-coral));
      &::placeholder {
         color: hsl(var(--clr-pm-coral) / 0.7);
      }
   }

   @include e(input) {
      padding-bottom: 0.8rem;
   }

   @include e(text-area) {
      padding-bottom: 3rem;
   }
}
