sources.styles/components/_button.scss:
@use 'abstracts' as *;
@use 'utilities' as *;

.btn {
   padding: 1em 3em;
   border-radius: 50px;
   @extend %text-dark;

   @include m(pm) {
      border: 2px solid;
      text-decoration: none;
      @include m(light) {
         @extend %text-white;
         @extend %bg-transparent;
         border-color: hsl($clr-pm-white);
         &:hover {
            @extend %bg-white;
            @extend %text-dark;
         }
      }

      @include m(dark) {
         @extend %bg-transparent;
         border-color: hsl($clr-dark-green);
         &:hover {
            @extend %bg-green-dark;
            @extend %text-white;
         }
      }
   }

   @include m(sd) {
      border: none;
      outline: none;
      @extend %bg-white;
      &:hover {
         @extend %bg-rapture;
      }
   }

   @include m(disabled) {
      opacity: 0.5;
   }
}

sources.styles/abstracts/_mixins.scss:
@use 'variables' as *;
@use 'maps' as *;

@mixin e($element) {
   &__#{$element} {
      @content;
   }
}

@mixin m($modifier) {
   &--#{$modifier} {
      @content;
   }
}

@mixin flex-parent($direction, $justify, $align) {
   flex-direction: $direction;
   justify-content: $justify;
   align-items: $align;
}

@mixin bg-img($img, $position, $size) {
   background-image: url('#{$img}');
   background-repeat: no-repeat;
   background-position: #{$position};
   background-size: #{$size};
}

@mixin p-el($el, $width: null, $height: null) {
   @if $el == 'before' or $el == 'after' {
      &::#{$el} {
         content: '';
         position: absolute;
         display: block;
         width: $width;
         height: $height;
         @content;
      }
   } @else {
      @error 'Before or after string required for valid pseudo element.';
   }
}

@mixin mq($key) {
   @media (min-width: map-get($breakpoints, $key)) {
      @content;
   }
}

@mixin fq($prop, $val) {
   @supports (#{$prop}: $val) {
      @content;
   }
}

sources.styles/components/_form.scss:
@use 'abstracts' as *;
@use 'utilities' as *;

.form {
   width: 100%;
   padding-top: 1rem;
   border: 0 0 0.1px solid hsl($clr-pm-white) 0;
   outline: none;
   @extend %bg-transparent;
   @extend %text-white;
   &::placeholder {
      color: hsl(0 0% 100% / 0.7);
   }
   &:hover,
   &:focus {
      border-bottom-color: hsl($clr-rapture-blue);
   }

   @include m(error) {
      border-bottom-color: hsl($clr-pm-coral);
      &::placeholder {
         color: hsl(0 87% 73% / 0.7);
      }
   }

   @include e(input) {
      padding-bottom: 0.8rem;
   }

   @include e(text-area) {
      padding-bottom: 3rem;
   }
}

sources.styles/components/_thin-border.scss:
@use 'abstracts' as *;
@use 'utilities' as *;

.small-border {
   @include p-el(before, 20px, 5px) {
      top: 0;
      left: 0;
      @extend %bg-coral;
   }
   .hero &::before {
      // change parent class
      @extend %bg-rapture;
   }
}
