sources.styles/abstracts/_variables.scss:
@use 'maps' as *;
@use 'mixins' as *;

:root {
   @each $color, $value in $colors {
      --clr-#{$color}: #{$value};
   }

   @each $screen-size, $map in $type-scale {
      @if $screen-size == small {
         @each $size, $value in $map {
            --#{$size}: #{$value};
         }
      } @else if $screen-size == large {
         @include mq($width: min-width, $key: tablet) {
            @each $size, $value in $map {
               --#{$size}: #{$value};
            }
         }
      } @else if $screen-size == lh-small {
         @each $size, $value in $map {
            --#{$size}: #{$value};
         }
      } @else if $screen-size == lh-large {
         @include mq($width: min-width, $key: tablet) {
            @each $size, $value in $map {
               --#{$size}: #{$value};
            }
         }
      } @else if $screen-size == weights {
         @each $size, $value in $map {
            --#{$size}: #{$value};
         }
      } @else if $screen-size == align-mobile {
         @each $size, $value in $map {
            --#{$size}: #{$value};
         }
      } @else if $screen-size == align-tablet {
         @include mq($width: min-width, $key: tablet) {
            @each $size, $value in $map {
               --#{$size}: #{$value};
            }
         }
      } @else if $screen-size == align-large {
         @include mq($width: min-width, $key: large) {
            @each $size, $value in $map {
               --#{$size}: #{$value};
            }
         }
      }
   }

   @each $prop, $value in $layout {
      --#{$prop}: #{$value};
   }
}

sources.styles/abstracts/_mixins.scss:
@use 'maps' as *;

// =========================================
// BEM
// =========================================

@mixin e($element) {
   &__#{$element} {
      @content;
   }
}

@mixin m($modifier) {
   &--#{$modifier} {
      @content;
   }
}

// =========================================
// flex
// =========================================

@mixin flexy($disp: flex, $dir: null, $wrap: null, $just: null, $align: null) {
   display: $disp;
   flex-direction: $dir;
   flex-wrap: $wrap;
   justify-content: $just;
   align-items: $align;
}

// =========================================
// box-model
// =========================================

@mixin box-model($disp: null, $width: null, $height: null) {
   display: $disp;
   width: $width;
   height: $height;
}

// =========================================
// position
// =========================================

@mixin position($pos, $width: null, $height: null, $z-index: null) {
   position: $pos;
   width: $width;
   height: $height;
   z-index: $z-index;
   @content;
}

// =========================================
// bg-img
// =========================================

@mixin bg-img($img: null, $position: null, $size: null) {
   background-image: url('#{$img}');
   background-repeat: no-repeat;
   background-position: $position;
   background-size: $size;
}

// =========================================
// pseudo-el
// =========================================

@mixin p-el(
   $el,
   $content: '',
   $position: absolute,
   $width: null,
   $height: null
) {
   @if $el == 'before' or $el == 'after' {
      &::#{$el} {
         content: $content;
         position: $position;
         display: block;
         width: $width;
         height: $height;
         @content;
      }
   } @else {
      @error 'Before or after string required for valid pseudo element.';
   }
}

// =========================================
// uppercase
// =========================================

@mixin uc() {
   text-transform: uppercase;
   letter-spacing: 1px;
   // easier to read when applied  to non-headings
}

// =========================================
// media query
// =========================================

@mixin mq($width, $key) {
   @if $width ==
      'max-width' or
      $width ==
      'min-width' or
      $key ==
      'tablet' or
      $key ==
      'large'
   {
      @media ($width: map-get($breakpoints, $key)) {
         @content;
      }
   } @else {
      @error 'Tablet, medium, large & valid width required for media query.';
   }
}

@mixin mqs($minWidth, $minKey, $maxWidth, $maxKey) {
   @media ($minWidth: map-get($breakpoints, $minKey)) and ($maxWidth: map-get($breakpoints, $maxKey)) {
      @content;
   }
}

// =========================================
// feature queries
// =========================================

@mixin fq($prop, $val) {
   @supports (#{$prop}: $val) {
      @content;
   }
}

@mixin fqs($selector) {
   @supports selector(#{$selector}) {
      @content;
   }
}

sources.styles/abstracts/_utilities.scss:
@use 'maps' as *;
@use 'mixins' as *;

@each $color, $value in $colors {
   @if $color ==
      sacramento-green or
      $color ==
      pm-white or
      $color ==
      pm-coral or
      $color ==
      rapture-blue or
      $color ==
      dark-green
   {
      .text-#{$color} {
         color: var(--clr-#{$color}, $value);
      }
   }
   .bg-#{$color} {
      background-color: var(--clr-#{$color}, $value);
   }
}

@each $screen-size, $map in $type-scale {
   @if $screen-size == small {
      @each $property, $value in $map {
         .#{$property} {
            font-size: var(--#{$property});
         }
      }
   }

   @if $screen-size == lh-small {
      @each $property, $value in $map {
         .#{$property} {
            line-height: var(--#{$property});
         }
      }
   }

   @if $screen-size == weights {
      @each $property, $value in $map {
         .#{$property} {
            font-weight: var(--#{$property});
         }
      }
   }

   @if $screen-size == align-mobile {
      @each $property, $value in $map {
         .#{$property} {
            text-align: var(--#{$property});
         }
      }
   }
}

// =========================================
// layout
// =========================================

.flow > *:where(:not(:first-child)) {
   margin-top: var(--flow-space, 1rem);
}

.sr-only {
   position: absolute;
   width: 1px;
   height: 1px;
   padding: 0;
   margin: -1px;
   overflow: hidden;
   clip: rect(0, 0, 0, 0);
   white-space: nowrap;
   border: 0;
}

.grid-container {
   display: grid;
}

.position-center {
   @include position($pos: absolute, $z-index: 2) {
      top: 50%;
      transform: translateY(-50%);
   }
}

