sources.styles/base/_reset.scss:
/* Box sizing rules */
*,
*::before,
*::after {
   box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
   margin: 0;
}

/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role='list'],
ol[role='list'] {
   list-style: none;
   padding: 0;
   margin: 0;
}

/* Set core root defaults */
html:focus-within {
   scroll-behavior: smooth;
}

/* Set core body defaults */
body {
   min-height: 100vh;
   text-rendering: optimizeSpeed;
   line-height: 1.5;
   overflow-x: hidden;
}

/* A elements that don't have a class get default styles */
a:not([class]) {
   text-decoration-skip-ink: auto;
}

/* Make images easier to work with */
img,
picture {
   max-width: 100%;
   display: block;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
   font: inherit;
}

/* Remove all animations and transitions for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
   html:focus-within {
      scroll-behavior: auto;
   }
   *,
   *::before,
   *::after {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
      scroll-behavior: auto !important;
   }
}

sources.styles/components/_button.scss:
@use 'abstracts' as *;

.btn {
   border-radius: 50px;
   transition: border 0.2s ease-in-out, background-color 0.2s ease-in-out,
      color 0.2s ease-in-out;

   // =========================================
   // primary
   // =========================================

   @include m(pm) {
      display: inline-block;
      padding: 0.3em 2.1em 0.5em;
      border: 2.5px solid;
      font-weight: var(--fw-medium);
      @include mq(min-width, tablet) {
         padding: 0.4em 2.1em 0.6em;
      }

      @include m(light) {
         @include mq(max-width, tablet) {
            margin-top: 1.3rem;
         }
         border-color: hsl(var(--clr-pm-white));
         &:hover {
            border-color: transparent;
            background-color: var(--clr-pm-white);
            color: var(--clr-dark-green);
         }
      }

      @include m(dark) {
         font-weight: var(--fw-semi-bold);
         &:hover {
            border-color: transparent;
            background-color: var(--clr-dark-green);
            color: var(--clr-pm-white);
         }
      }
   }

   // =========================================
   // secondary
   // =========================================

   @include m(sd) {
      padding: 0.5em 2em;
      border: none;
      outline: none;
      &:hover {
         background-color: var(--clr-rapture-blue);
      }
   }

   // =========================================
   // About
   // =========================================

   @include m(director) {
      @include position(
         $pos: absolute,
         $width: 70px,
         $height: 70px,
         $z-index: 3
      ) {
         bottom: -34px;
         left: 50%;
         transform: translateX(-50%);
      }
      border-radius: 50%;
      background-color: var(--clr-pm-coral);
      transition: background-color 0.25s ease-in-out;
      @include bg-img('../assets/about/icon-cross.svg', center, 20px);
      &:hover {
         background-color: var(--clr-rapture-blue);
      }

      &[aria-selected='true'] {
         background-color: var(--clr-rapture-blue);
         @include bg-img('../assets/about/icon-close.svg');
      }
   }

   // =========================================
   // Contact
   // =========================================

   @include m(contact) {
      padding: 0.7em 2em;
      @include m(wide) {
         @include mq(max-width, tablet) {
            display: none;
         }
         right: 0;
         @include mq(min-width, large) {
            right: 4rem;
         }
         @include m(ct) {
            font-size: 1rem;
            @include mq(min-width, large) {
               right: 0;
            }
         }
      }
   }

   @include m(disabled) {
      opacity: 0.5;
   }

   // =========================================
   // All
   // =========================================

   @include m(cta) {
      @include mq(min-width, tablet) {
         padding: 0.6em 2.2em;
      }
   }
}

sources.styles/components/_sidebar.scss:
@use 'abstracts' as *;

.sidebar {
   display: none;
   @include mq(max-width, tablet) {
      display: block;
      padding: min(10rem, 20vh) 3rem;
      width: 310px;
      position: fixed;
      inset: 0 0 0 calc(100% - 310px);
      z-index: 1000;
      transform: translateX(100%);
      overflow-x: hidden;
      transition: transform 0.5s ease-in-out;
      @include bg-img(
         $img: '../assets/bg/bg-pattern-about-1-mobile-nav-1.svg',
         $position: calc(100% + 120px) 100%,
         $size: 240px
      );
      &[data-visible='true'] {
         transform: translateX(0);
      }
   }
}

// Overlay

body {
   @include mq(max-width, tablet) {
      @include p-el('before', $position: fixed) {
         inset: 0;
         z-index: 999;
         background-color: rgba(0, 0, 0, 0.55);
         opacity: 0;
         pointer-events: none;
         transition: opacity 0.5s ease-in-out;
      }
      &[data-visible='true'] {
         overflow-y: hidden;
         @include p-el('before') {
            opacity: 1;
            pointer-events: all;
         }
      }
   }
}

