      border-bottom: 1px solid var(--clr-profile-border);
   }

   @include e(input) {
      padding: 1.8rem 0.7rem 1.2rem;
   }

   @include e(text-area) {
      resize: none;
      padding: 2rem 0.7rem 3rem;
      margin-bottom: 1.5rem;
      @include mq(min-width, large) {
         padding: 1.5rem 0.7rem 2rem;
         margin-bottom: 1.5rem;
      }
   }

   @include m(label) {
      position: relative;
      display: block;
   }

   @include m(error) {
      font-style: italic;
      font-size: 0.7rem;
      font-weight: var(--fw-semi-bold);
      color: var(--clr-pm-coral);
      white-space: nowrap;
      @include position($pos: absolute) {
         bottom: -102px;
         left: 15px;
      }
   }

   &[data-error='true'] {
      border-bottom-color: var(--clr-pm-coral);
      &::placeholder {
         color: var(--clr-pm-coral);
      }
   }
}

sources.styles/layout/_header.scss:
@use 'abstracts' as *;

.header {
   @include m(pm) {
      padding: var(--ct-padding-hdr);
   }

   @include e(nav--container) {
      position: relative;
      min-height: 2rem;
   }

   @include m(home) {
      --ct-padding-hdr: 4rem 0 min(40vh, 18rem);
      @include mq(max-width, large) {
         @include bg-img(
            '../assets/bg/bg-pattern-home-2.svg',
            center 100%,
            430px 130px
         );
      }
      @include mq(min-width, tablet) {
         background-size: 400px 120px;
      }
      @include mq(min-width, large) {
         background-image: url('../assets/bg/bg-pattern-home-1.svg');
         background-repeat: no-repeat;
         background-position: calc(0% - 100px) 90%;
         background-size: 200px;
      }
      @include mq(min-width, align-mw) {
         background-position: calc(0% - 100px) 37%;
      }
   }

   @include m(about) {
      @include bg-img(
         '../assets/bg/bg-pattern-about-1-mobile-nav-1.svg',
         calc(100% + 105px) calc(100% + 105px),
         210px
      );
      @include mq(min-width, tablet) {
         background-position: calc(100% + 95px) 100%;
         background-size: 190px;
      }
      @include mq(min-width, large) {
         --ct-padding-hdr: 4rem 0 min(17vh, 10rem);
         background-position: calc(100% + 113px) 100%;
         background-size: 170px 180px;
      }
      @include mq(min-width, align-mw) {
         background-position: calc(100% + 85px) 100%;
      }
   }
}

sources.styles/layout/_nav.scss:
@use 'abstracts' as *;

nav {
   & a {
      transition: color 0.2s ease-in-out;
      &:hover {
         color: var(--clr-pm-coral);
      }
   }
}

.nav {
   @include m(sd) {
      @include flexy($align: center);
      --gap: 2rem;
      gap: var(--gap);
   }

   @include m(wide) {
      @include mq(max-width, tablet) {
         display: none;
      }
      left: 190px;
      @include e(pm) {
         & > * {
            display: inline-block;
            &:not(:last-child) {
               --gap: 1.5rem;
               margin-right: var(--gap);
            }
         }
      }
   }

   @include m(contact) {
      font-size: 1rem;
   }
}

sources.styles/pages/_about.scss:
@use 'abstracts' as *;

.grid-container--directors {
   gap: var(--gap-directors);
   grid-template-columns: 1fr;
   grid-auto-rows: auto;
   @include mq(min-width, tablet) {
      grid-template-columns: repeat(2, 1fr);
   }
   @include mqs(min-width, tablet, max-width, large) {
      width: 90%;
      margin: 0 auto;
   }
   @include mq(min-width, large) {
      grid-template-columns: repeat(3, 1fr);
   }
   @include e(item) {
      position: relative;
      padding: 2rem 0 4rem;
      background-color: hsl(186 96% 10%);
   }
}

.extra-info {
   @include position(absolute, $z-index: 2);
   inset: 0;
   @include flexy($dir: column, $align: center);
   padding-top: 2rem;
   opacity: 0;
   pointer-events: none;
   transition: opacity 0.3s linear;
   &[data-visible='true'] {
      opacity: 1;
      pointer-events: all;
   }
   @include mq(max-width, tablet) {
      padding-top: 2.6rem;
   }
}

sources.styles/pages/_home.scss:
@use 'abstracts' as *;

.build--parent {
   @include e(child) {
      &:first-child {
         @include mq(max-width, large) {
            padding-top: 3rem;
         }
      }
      &:last-child {
         @include mq($width: min-width, $key: tablet) {
            text-align: left;
         }
      }
   }
}

.testimonials--parent {
   @include e(quotes) {
      & article {
         padding-top: 3.1rem;
         @include flexy($dir: column, $align: center);
         @include mq(min-width, tablet) {
            padding-top: 3.4rem;
         }
         @include mq(min-width, large) {
            padding-top: 3.5rem;
         }
         & .quote {
            padding-top: 0.5rem;
            margin-bottom: 1rem;
            @include mq(min-width, large) {
               width: 100%;
               margin-bottom: 1rem;
            }
         }
      }
   }
}
