@use 'maps' as *;
@use 'mixins' as *;

:root {
   @each $color, $value in $colors {
      --clr-#{$color}: #{$value};
   }

   @each $screen-size, $map in $type-scale {
      @if $screen-size == small {
         @each $size, $value in $map {
            --#{$size}: #{$value};
         }
      } @else if $screen-size == large {
         @include mq($width: min-width, $key: tablet) {
            @each $size, $value in $map {
               --#{$size}: #{$value};
            }
         }
      } @else if $screen-size == lh-small {
         @each $size, $value in $map {
            --#{$size}: #{$value};
         }
      } @else if $screen-size == lh-large {
         @include mq($width: min-width, $key: tablet) {
            @each $size, $value in $map {
               --#{$size}: #{$value};
            }
         }
      } @else if $screen-size == weights {
         @each $size, $value in $map {
            --#{$size}: #{$value};
         }
      } @else if $screen-size == align-mobile {
         @each $size, $value in $map {
            --#{$size}: #{$value};
         }
      } @else if $screen-size == align-tablet {
         @include mq($width: min-width, $key: tablet) {
            @each $size, $value in $map {
               --#{$size}: #{$value};
            }
         }
      } @else if $screen-size == align-large {
         @include mq($width: min-width, $key: large) {
            @each $size, $value in $map {
               --#{$size}: #{$value};
            }
         }
      }
   }

   @each $prop, $value in $layout {
      --#{$prop}: #{$value};
   }
}

sources.styles/abstracts/_mixins.scss:
@use 'maps' as *;

// =========================================
// BEM
// =========================================

@mixin e($element) {
   &__#{$element} {
      @content;
   }
}

@mixin m($modifier) {
   &--#{$modifier} {
      @content;
   }
}

// =========================================
// flex
// =========================================

@mixin flexy($disp: flex, $dir: null, $wrap: null, $just: null, $align: null) {
   display: $disp;
   flex-direction: $dir;
   flex-wrap: $wrap;
   justify-content: $just;
   align-items: $align;
}

// =========================================
// box-model
// =========================================

@mixin box-model($disp: null, $width: null, $height: null) {
   display: $disp;
   width: $width;
   height: $height;
}

// =========================================
// position
// =========================================

@mixin position($pos, $width: null, $height: null, $z-index: null) {
   position: $pos;
   width: $width;
   height: $height;
   z-index: $z-index;
   @content;
}

// =========================================
// bg-img
// =========================================

@mixin bg-img($img: null, $position: null, $size: null) {
   background-image: url('#{$img}');
   background-repeat: no-repeat;
   background-position: $position;
   background-size: $size;
}

// =========================================
// pseudo-el
// =========================================

@mixin p-el(
   $el,
   $content: '',
   $position: absolute,
   $width: null,
   $height: null
) {
   @if $el == 'before' or $el == 'after' {
      &::#{$el} {
         content: $content;
         position: $position;
         display: block;
         width: $width;
         height: $height;
         @content;
      }
   } @else {
      @error 'Before or after string required for valid pseudo element.';
   }
}

// =========================================
// social-icons
// =========================================

@mixin social-icon() {
   transition: transform 200ms linear, color 130ms linear;
   &:hover,
   &:focus {
      color: var(--clr-pm-coral);
      transform: scale(1.1);
   }
}

// =========================================
// media query
// =========================================

@mixin mq($width, $key) {
   @if $width ==
      'max-width' or
      $width ==
      'min-width' or
      $key ==
      'tablet' or
      $key ==
      'large'
   {
      @media ($width: map-get($breakpoints, $key)) {
         @content;
      }
   } @else {
      @error 'Tablet, medium, large & valid width required for media query.';
   }
}

// =========================================
// feature queries
// =========================================

@mixin fq($prop, $val) {
   @supports (#{$prop}: $val) {
      @content;
   }
}

@mixin fqs($selector) {
   @supports selector(#{$selector}) {
      @content;
   }
}

