sources.styles/components/_button.scss:
@use 'abstracts' as *;

.btn {
   border-radius: 50px;
   transition: border 0.2s ease-in-out, background-color 0.2s ease-in-out,
      color 0.2s ease-in-out;

   // =========================================
   // primary
   // =========================================

   @include m(pm) {
      display: inline-block;
      padding: 0.4em 2.1em;
      border: 2px solid;

      @include m(light) {
         @include mq(max-width, tablet) {
            margin-top: 1.3rem;
         }
         // color: white
         border-color: hsl(var(--clr-pm-white));
         &:hover {
            background-color: var(--clr-pm-white);
            color: var(--clr-dark-green);
            border-color: transparent;
         }
      }

      @include m(dark) {
         // @extend %text-dark;
         // @extend %bg-transparent;
         border-color: hsl(var(--clr-dark-green));
         &:hover {
            background-color: var(--clr-dark-green);
            color: var(--clr-pm-white);
         }
      }
   }

   // =========================================
   // secondary
   // =========================================

   @include m(sd) {
      // @extend %text-dark;
      // @extend %bg-white;
      padding: 0.5em 2em;
      border: none;
      outline: none;
      &:hover {
         background-color: var(--clr-rapture-blue);
      }
   }

   @include m(contact) {
      padding: 0.7em 2em;
   }

   @include m(disabled) {
      opacity: 0.5;
   }
}

sources.styles/components/_sidebar.scss:
@use 'abstracts' as *;

.sidebar {
   display: none;
   @include mq(max-width, tablet) {
      display: block;
      padding: min(10rem, 20vh) 3rem;
      position: fixed;
      inset: 0 0 0 calc(100% - 347px);
      width: 347px;
      z-index: 1000;
      overflow-x: hidden;
      transform: translateX(100%);
      transition: transform 0.5s ease-in-out;
      @include bg-img(
         $img: '../assets/bg/bg-pattern-about-1-mobile-nav-1.svg',
         $position: calc(100% + 153px) 100%,
         $size: 300px
      );
      & li:nth-child(2) {
         margin-left: 0.4rem;
      }
      &[data-visible='true'] {
         transform: translateX(0);
      }
   }
}

// Overlay

body {
   @include mq(max-width, tablet) {
      @include p-el('before', $position: fixed) {
         inset: 0;
         z-index: 999;
         background-color: rgba(0, 0, 0, 0.55);
         opacity: 0;
         pointer-events: none;
         transition: opacity 0.5s ease-in-out;
      }
      &[data-visible='true'] {
         overflow-y: hidden;
         @include p-el('before') {
            opacity: 1;
            pointer-events: all;
         }
      }
   }
}

